// Retrieve current state of global timer variables
var gameState = global.get("gameState") || 'playing';
var gameDuration = global.get("gameDuration") || 0;
var elapsedTimeDelta = global.get("elapsedTimeDelta") || 0;
// Total time elapsed is duration since the start button was last pressed, + any adjustments
var timeElapsed = 0;
var startTime = flow.get("startTime") || 0;

// If the game is in progress, update the time passed since start button was last pressed
if(gameState == "playing") {
    timeElapsed = (Date.now() - startTime);
}

// We update these even if gameState is not playing, to allow for
// adjustments to gameDuration or timeElapsed made while game is stopped
var timeElapsedAfterAdjustments = timeElapsed + elapsedTimeDelta;
var timeRemaining = gameDuration - timeElapsedAfterAdjustments;

// Update the global variable so that it is available to other nodes if required
global.set("timeRemaining", timeRemaining);

// Update the msg to carry string
msg.timeElapsed = timeElapsedAfterAdjustments;        
msg.timeRemaining = timeRemaining;

// Display status nof this node in the flow
node.status({ fill: "green", shape: "dot", text: timeRemaining });

// Only need to update UI if timeRemaining has changed
return msg;

// Create formattedTime in hh:mm:ss
var t = msg.timeElapsed / 1000; //ms
var h = Math.floor(t / 3600);
var m = Math.floor(t % 3600 / 60);
var s = Math.floor(t % 3600 % 60);
msg.formattedTimeElapsed = ("0" + h).slice(-2) + ":" + ("0" + m).slice(-2) + ":" + ("0" + s).slice(-2);

var t2 = msg.timeRemaining / 1000; //ms
var h2 = Math.floor(t2 / 3600);
var m2 = Math.floor(t2 % 3600 / 60);
var s2 = Math.floor(t2 % 3600 % 60);
msg.formattedTimeRemaining = ("0" + h2).slice(-2) + ":" + ("0" + m2).slice(-2) + ":" + ("0" + s2).slice(-2);

var t3 = global.get("gameDuration") / 1000; //ms
var h3 = Math.floor(t3 / 3600);
var m3 = Math.floor(t3 % 3600 / 60);
var s3 = Math.floor(t3 % 3600 % 60);
msg.formattedGameDuration = ("0" + h3).slice(-2) + ":" + ("0" + m3).slice(-2) + ":" + ("0" + s3).slice(-2);

// Set timeColour depending on current gamestate and time remaining
var gameState = global.get("gameState");
if (gameState == "playing") {
    if (msg.timeRemaining < 60000) { msg.timeColour = "red"; }
    else { msg.timeColour = "green"; }
}
else { msg.timeColour = "gray"; }

// Update the editor node
node.status({ fill: msg.timeColour, shape:"dot", text:msg.formattedTimeRemaining});

// Forward the message along the flow
return msg;

let messages = [];

if (msg.payload === 'show_door') {
